                    <td>
                      {ex.status === "RUNNING" && (
                        <button
                          className="btn btn-danger btn-sm"
                          onClick={() => stopExecution(ex.execution_arn, ex.name)}
                          disabled={busy === ex.execution_arn}
                        >
                          {busy === ex.execution_arn ? "Stopping..." : "Stop"}
                        </button>
                      )}
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          )}
        </div>
      </div>

      {/* ── Pipeline Runs ──────────────────────────────────────────────── */}
      <div className="panel stagger-in">
        <div className="panel-header">
          <h3>Pipeline Runs</h3>
          <div className="flex-row">
            <button
              className="btn btn-accent"
              onClick={handleNewRun}
              disabled={busy === "new-run"}
            >
              {busy === "new-run" ? "Starting..." : "New Run"}
            </button>
            <button
              className="btn"
              onClick={handleSeed}
              disabled={busy === "seed"}
            >
              {busy === "seed" ? "Seeding..." : "Seed"}
            </button>
          </div>
        </div>
        <div className="panel-body dense">
          {runs.length === 0 ? (
            <div className="empty-state">No pipeline runs found</div>
          ) : (
            <table className="data-table">
              <thead>
                <tr>
                  <th>Run ID</th>
                  <th>Created</th>
                  <th>Stages</th>
                  <th>Notes</th>
                  <th></th>
                </tr>
              </thead>
              <tbody>
                {runs.map((r) => {
                  const stageMap = new Map(
                    r.stages.map((s) => [s.stage, s.status]),
                  );
                  return (
                    <tr key={r.run_id}>
                      <td className="td-mono">{r.run_id}</td>
                      <td>{timeAgo(r.created_at)}</td>
                      <td>
                        <div className="stage-chips">
                          {STAGE_ORDER.map((n) => {
                            const status = stageMap.get(n);
                            return (
                              <span
                                key={n}
                                className={status ? stageStatusClass(status) : "stage-chip idle"}
                                title={`Stage ${n}: ${status || "not started"}`}
                              >
                                {n}
                              </span>
                            );
                          })}
                        </div>
                      </td>
                      <td className="td-muted">{r.notes || "—"}</td>
                      <td>
                        <button
                          className="btn btn-danger btn-sm"
                          onClick={() => deleteRun(r.run_id)}
                          disabled={busy === r.run_id}
                        >
                          {busy === r.run_id ? "..." : "Delete"}
                        </button>
                      </td>
                    </tr>
                  );
                })}
              </tbody>
            </table>
          )}
        </div>
      </div>
    </div>
  );
}

